package com.example.mybaby6;import android.os.Bundle;import android.text.Editable;import android.text.TextWatcher;import android.util.Log;import android.view.View;import android.widget.AdapterView;import android.widget.ArrayAdapter;import android.widget.EditText;import android.widget.ImageView;import android.widget.ListView;import androidx.appcompat.app.AppCompatActivity;import java.util.ArrayList;public class home_page extends AppCompatActivity {    private static final String TAG = "home_page";    ArrayAdapter adapter;    ImageView imageView;    ArrayList<String> basket;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.home_page);        EditText thefilter = (EditText) findViewById(R.id.searchFilter);        ListView list = (ListView) findViewById(R.id.theList);        imageView = (ImageView) findViewById(R.id.imageView);        Log.d(TAG, "onCreate: Started.");        ArrayList<String> products = new ArrayList<>();        basket = new ArrayList<>();        products.add("Молоко Домик в Деревне");        products.add("Молоко Коровка");        products.add("Киндер Пингви");        products.add("Твикс");        adapter = new ArrayAdapter(this, R.layout.list_item_layout, products);        list.setAdapter(adapter);        thefilter.addTextChangedListener(new TextWatcher() {            @Override            public void beforeTextChanged(CharSequence charSequence, int i, int i1, int i2) {                imageView.setVisibility(View.INVISIBLE);            }            @Override            public void onTextChanged(CharSequence charSequence, int i, int i1, int i2) {                (home_page.this).adapter.getFilter().filter(charSequence);            }            @Override            public void afterTextChanged(Editable editable) {            }        });        list.setOnItemClickListener(new AdapterView.OnItemClickListener() {            @Override            public void onItemClick(AdapterView<?> arg0, View arg1, int position, long arg3) {                basket.add((String) adapter.getItem(position));            }        });    }}